USERTRAINCODE.Data:
/*---------------------------------------------------------------*
*
* Setup the CAS information
*
* Note that the casHost and casPort can be specified in the EM project
* startup code using the em_casHost and em_casPort macro variables.
*
*   %let casHost =;
*   %let casPort =;
*
*----------------------------------------------------------------*/

   %let caslib  =;
   %let casuser =;

/*---------------------------------------------------------------*
*
* Create a CAS session and assign the CAS library.
*
*----------------------------------------------------------------*/
   %em_viya_signon;

/*---------------------------------------------------------------*
*
* Prepare the training data and loads it in CAS
* The em_viya_dataprep macro a CAS library has been
* assigned.
* em_casdata= macro variable identifying the training data
* em_casLib = macro variable identifying the CAS library
*
*----------------------------------------------------------------*/
   %em_viya_dataprep;

/*---------------------------------------------------------------*
*
* Setting up macro variables and macros in SAS Viya
*
* Variable Macros:
*    em_vars              = all variables
*    em_class             = class variables
*    em_interval          = interval variables
*    em_interval_input    = interval inputs
*    em_binary_input      = binary inputs
*    em_nominal_input     = nominal inputs
*    em_ordinal_input     = ordinal inputs
*    em_interval_rejected = interval rejected
*    em_binary_rejected   = binary rejected
*    em_nominal_rejected  = nominal rejected
*    em_ordinal_rejected  = ordinal rejected
*    em_id                = id variables
*    em_key               = key variable
*
* Variable:
*    em_num_vars              = number of variables
*    em_num_class             = number of class variables
*    em_num_interval          = number of interval variables
*    em_num_interval_input    = number of interval inputs
*    em_num_binary_input      = number of binary inputs
*    em_num_nominal_input     = number of nominal inputs
*    em_num_ordinal_input     = number of ordinal inputs
*    em_num_interval_rejected = number of interval rejected
*    em_num_binary_rejected   = number of binary rejected
*    em_num_nominal_rejected  = number of nominal rejected
*    em_num_ordinal_rejected  = number of ordinal rejected
*    em_num_id                = number of id variables
*    em_num_key               = number of key variable
*    em_partitionvar          = partition variable
*
* Target information:
*    em_dec_target        = target variable
*    em_dec_level         = measurement level of target variable
*
* Utility:
*
*    em_partition_statement = partition statement
*
* Files:
*    em_viya_nodedir      = temporary node folder
*    em_file_scorecode    = score code file
*    em_file_cdelta_train = metadata file to modify the metadata exported by the node
*    em_file_odsfile      = ods output file
*    em_file_astore       = local analytic store file
*    em_data_rstore       = analytic store CAS table
*    em_data_viyaroc      = ROC CAS table;
*    em_data_viyalift     = Lift CAS table;
*    em_data_viyafitstat  = Fit Statistic CAS table
*
*----------------------------------------------------------------*/

%em_register(key=varImportance1, type=DATA);
%em_register(key=nnetmodel, type=DATA, cas=y);
 
%em_viya_setMacroVars;
 
/*---------------------------------------------------------------*
*
* User Code
*
*----------------------------------------------------------------*/
 
/* Neural Network */
 
   proc nnet data=&amp;em_casdata;
      target %em_target /level=&amp;em_dec_level;
      hidden 3;
      input %em_interval_input / level=interval;
      input %em_nominal_input  / level=nominal;
      train outmodel=&amp;em_user_nnetmodel;
      architecture MLP;
      optimization alg=SGD;
      autotune maxiters=5 maxevals=5 maxtime=200 popsize=2;
      &amp;em_partition_statement;
      code file=&quot;&amp;em_file_scorecode&quot;;
      score out=&amp;em_casout copyvars=(%em_target &amp;em_partitionvar);
   run;

%em_viya_assess(name=Neural);
 
/* Genaralized Linear Model */
 
   proc genselect data=&amp;em_casdata;
	   class %em_nominal_input;
	   model %em_target=%em_interval_input %em_nominal_input / dist=normal;
	   selection method=forward(select=sbc stop=sbc choose=sbc) hierarchy=none;
      &amp;em_partition_statement;
	   output out=&amp;em_casout xbeta predicted lower upper;
	   code file=&quot;&amp;em_file_scorecode&quot;;
   run;
 
%em_viya_assess(name=GLM);
 
/* Forest */
 
   proc forest data=&amp;em_casdata;
      target %em_target /level=&amp;em_dec_level;
      input %em_interval_input / level=interval;
      input %em_nominal_input  / level=nominal;
      &amp;em_partition_statement;
      savestate rstore=&amp;em_data_rstore;
   run;
 
%em_viya_assess(name=Forest);
 
/* Gradient Boosting */
 
   proc gradboost data=&amp;em_casdata outmodel=&amp;em_caslib..outmodel seed=1 nTree=10 missing=MACSMALL;
	   target %em_target / level=&amp;em_dec_level;
      input %em_interval_input / level=interval;
      input %em_nominal_input  / level=nominal;
	   &amp;em_partition_statement;
      code file=&quot;&amp;em_file_scorecode&quot;;
	   score out=&amp;em_casout copyvars=(%em_target &amp;em_partitionvar);
   run;
 
%em_viya_assess(name=GB);
 
/* Decision Tree */

      proc treesplit data=&amp;em_casdata;
         target %em_target /level=&amp;em_dec_level;
         input %em_interval_input / level=interval;
         input %em_nominal_input  / level=nominal;
         prune reducederror;
         &amp;em_partition_statement;
         code file=&quot;&amp;em_file_scorecode&quot;;
         ods output variableImportance=&amp;em_user_varimportance1;
      run;

  %em_viya_assess(name=Tree1);
 
/* Linear Regression */
 
   proc regselect data=&amp;em_casdata;
	   class %em_nominal_input;
	   model %em_target=%em_interval_input %em_nominal_input /;
	   selection method=backward (select=sbc stop=sbc choose=sbc) hierarchy=none;
	   output out=&amp;em_casout p=predicted lcl=lcl ucl=ucl;
	   code file=&quot;&amp;em_file_scorecode&quot;;
   run;
 
%em_viya_assess(name=LinearRegression);
 
%em_viya_modelselection;
 
/*---------------------------------------------------------------*
*
* The em_viya_report macro processes assessment tables, as
* well as table and files specified via the em_register macro and downloads
* them to the EM diagram and node folders.
*
*----------------------------------------------------------------*/
%em_report(key=varImportance1, viewtype=DATA, description=Variable Importance Tree1,  Block=SAS Viya Reports);
%em_report(key=nnetmodel, viewtype=DATA, description=NNET Outmodel,  Block=SAS Viya Reports);
%em_viya_report;
 
/*---------------------------------------------------------------*
*
* The em_viya_terminate macro terminates the CAS session.
*
*----------------------------------------------------------------*/
%em_viya_terminate;

/*---------------------------------------------------------------* * * Setup the CAS information * * Note that the casHost and casPort can be specified in the EM project * startup code using the em_casHost and em_casPort macro variables. * * %let casHost =; * %let casPort =; * *----------------------------------------------------------------*/
%let cashost = rdcesx05065.race.sas.com; %let casport = 5570;
%let caslib =CASUSER; %let casuser =qstinst;
/*---------------------------------------------------------------* * * Create a CAS session and assign the CAS library. * *----------------------------------------------------------------*/ %em_viya_signon;
/*---------------------------------------------------------------* * * Prepare the training data and loads it in CAS * The em_viya_dataprep macro a CAS library has been * assigned. * em_casdata= macro variable identifying the training data * em_casLib = macro variable identifying the CAS library * *----------------------------------------------------------------*/ %em_viya_dataprep;
/*---------------------------------------------------------------* * * Setting up macro variables and macros in SAS Viya * * Variable Macros: * em_vars = all variables * em_class = class variables * em_interval = interval variables * em_interval_input = interval inputs * em_binary_input = binary inputs * em_nominal_input = nominal inputs * em_ordinal_input = ordinal inputs * em_interval_rejected = interval rejected * em_binary_rejected = binary rejected * em_nominal_rejected = nominal rejected * em_ordinal_rejected = ordinal rejected * em_id = id variables * em_key = key variable * * Variable: * em_num_vars = number of variables * em_num_class = number of class variables * em_num_interval = number of interval variables * em_num_interval_input = number of interval inputs * em_num_binary_input = number of binary inputs * em_num_nominal_input = number of nominal inputs * em_num_ordinal_input = number of ordinal inputs * em_num_interval_rejected = number of interval rejected * em_num_binary_rejected = number of binary rejected * em_num_nominal_rejected = number of nominal rejected * em_num_ordinal_rejected = number of ordinal rejected * em_num_id = number of id variables * em_num_key = number of key variable * em_partitionvar = partition variable * * Target information: * em_dec_target = target variable * em_dec_level = measurement level of target variable * * Utility: * * em_partition_statement = partition statement * * Files: * em_viya_nodedir = temporary node folder * em_file_scorecode = score code file * em_file_cdelta_train = metadata file to modify the metadata exported by the node * em_file_odsfile = ods output file * em_file_astore = local analytic store file * em_data_rstore = analytic store CAS table * em_data_viyaroc = ROC CAS table; * em_data_viyalift = Lift CAS table; * em_data_viyafitstat = Fit Statistic CAS table * *----------------------------------------------------------------*/
%em_register(key=nnetmodel, type=DATA, cas=y);
%em_viya_setMacroVars;
/*---------------------------------------------------------------* * * User Code * *----------------------------------------------------------------*/
/* Neural Network */
proc nnet data=&amp;em_casdata; target %em_target /level=&amp;em_dec_level; hidden 2; input %em_interval_input / level=interval; input %em_nominal_input / level=nominal; train outmodel=&amp;em_user_nnetmodel; architecture MLP; optimization alg=LBFGS; autotune maxiters=5 maxevals=5 maxtime=200 popsize=2; &amp;em_partition_statement; code file=&quot;&amp;em_file_scorecode&quot;; score out=&amp;em_casout copyvars=(%em_target); run;
/*---------------------------------------------------------------* * * If the node is a modeling node, you can call the em_viya_assess macro * to create assessment results using the ASSESS procedure. This includes * the creation of lift, roc, and fit statistics table when applicable. * *----------------------------------------------------------------*/ %em_viya_assess(name=Neural);
/*---------------------------------------------------------------* * * The em_viya_report macro processes assessment tables, as * well as table and files specified via the em_register macro and downloads * them to the EM diagram and node folders. * *----------------------------------------------------------------*/ %em_report(key=nnetmodel, viewtype=DATA, description=NNET Outmodel, Block=SAS Viya Reports); %em_viya_report;
/*---------------------------------------------------------------* * * The em_viya_terminate macro terminates the CAS session. * *----------------------------------------------------------------*/ %em_viya_terminate;

/*---------------------------------------------------------------* * * Setup the CAS information * * Note that the casHost and casPort can be specified in the EM project * startup code using the em_casHost and em_casPort macro variables. * * %let casHost =; * %let casPort =; * *----------------------------------------------------------------*/
%let cashost = rdcesx05065.race.sas.com; %let casport = 5570;
%let caslib =CASUSER; %let casuser =qstinst;
/*---------------------------------------------------------------* * * Create a CAS session and assign the CAS library. * *----------------------------------------------------------------*/ %em_viya_signon;
/*---------------------------------------------------------------* * * Prepare the training data and loads it in CAS * The em_viya_dataprep macro a CAS library has been * assigned. * em_casdata= macro variable identifying the training data * em_casLib = macro variable identifying the CAS library * *----------------------------------------------------------------*/ %em_viya_dataprep;
/*---------------------------------------------------------------* * * Setting up macro variables and macros in SAS Viya * * Variable Macros: * em_vars = all variables * em_class = class variables * em_interval = interval variables * em_interval_input = interval inputs * em_binary_input = binary inputs * em_nominal_input = nominal inputs * em_ordinal_input = ordinal inputs * em_interval_rejected = interval rejected * em_binary_rejected = binary rejected * em_nominal_rejected = nominal rejected * em_ordinal_rejected = ordinal rejected * em_id = id variables * em_key = key variable * * Variable: * em_num_vars = number of variables * em_num_class = number of class variables * em_num_interval = number of interval variables * em_num_interval_input = number of interval inputs * em_num_binary_input = number of binary inputs * em_num_nominal_input = number of nominal inputs * em_num_ordinal_input = number of ordinal inputs * em_num_interval_rejected = number of interval rejected * em_num_binary_rejected = number of binary rejected * em_num_nominal_rejected = number of nominal rejected * em_num_ordinal_rejected = number of ordinal rejected * em_num_id = number of id variables * em_num_key = number of key variable * em_partitionvar = partition variable * * Target information: * em_dec_target = target variable * em_dec_level = measurement level of target variable * * Utility: * * em_partition_statement = partition statement * * Files: * em_viya_nodedir = temporary node folder * em_file_scorecode = score code file * em_file_cdelta_train = metadata file to modify the metadata exported by the node * em_file_odsfile = ods output file * em_file_astore = local analytic store file * em_data_rstore = analytic store CAS table * em_data_viyaroc = ROC CAS table; * em_data_viyalift = Lift CAS table; * em_data_viyafitstat = Fit Statistic CAS table * *----------------------------------------------------------------*/ %em_register(key=regfitstats, type=DATA);
%em_viya_setmacrovars;
/*---------------------------------------------------------------* * * User Code * *----------------------------------------------------------------*/
/* Linear Regression */
proc regselect data=&amp;em_casdata; class %em_nominal_input; model %em_target=%em_interval_input %em_nominal_input /; selection method=stepwise (select=sbc stop=sbc choose=sbc) hierarchy=single; output out=&amp;em_casout p=predicted lcl=lcl ucl=ucl; code file=&quot;&amp;em_file_scorecode&quot;; ods output FitStatistics=&amp;em_user_regfitstats; run;
/*---------------------------------------------------------------* * * If the node is a modeling node, you can call the em_viya_assess macro * to create assessment results using the ASSESS procedure. This includes * the creation of lift, roc, and fit statistics table when applicable. * *----------------------------------------------------------------*/ %em_viya_assess;
/*---------------------------------------------------------------* * * The em_viya_report macro processes assessment tables, as * well as table and files specified via the em_register macro and downloads * them to the EM diagram and node folders. * *----------------------------------------------------------------*/ %em_report(key=regfitstats, viewtype=DATA, description=Variable Importance Tree1, Block=SAS Viya Reports);
%em_viya_report;
/*---------------------------------------------------------------* * * The em_viya_terminate macro terminates the CAS session. * *----------------------------------------------------------------*/ %em_viya_terminate;
